package br.com.springboot.awesome.endpoint;import br.com.springboot.awesome.model.Student;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import java.util.Arrays;import java.util.List;import static java.util.Arrays.asList;@RestController@RequestMapping ("student")public class StudentEndPoint {    @RequestMapping (method = RequestMethod.GET, path= "/list")    public List<Student> ListAll () {        return Arrays.asList(new Student ("Doku"), new Student ("Todoroki"));    }}